import java.io.*; 
import java.util.*; 
import java.lang.*;

class Node{
	public int start;
	public int end;
	public Node left;
	public Node right;
	public int [] count;
	public Node (int start, int end) {
		count = new int[26];
		this.start = start;
		this.end = end;
	}
}


class Solution {
	
	public Node maketree(char[]c, int start, int end) {
		Node root = new Node (start, end);
		if (start == end) {
			root.count[c[start]-'a'] = 1;
			return root;
		}
		int mid = start + end; 
		mid /= 2;
		root.left = maketree(c, start, mid);
		root.right= maketree(c, mid+1, end);
		
		for (int i=0; i<26; i++) {
			root.count[i] = root.left.count[i]+root.right.count[i];
		}
		return root;
	}
	public int[] countchar(Node root, int start, int end) {
		int[] result = new int [26];
		if (root == null ||( start > root.end || end < root.start)) {
			return result;
		}
		if (start <= root.start && root.end <= end) {
			return root.count;
		}
		
		int[] L = countchar(root.left, start, end);
		int[] R = countchar(root.right, start, end);
		for (int i=0; i<26; i++) {
			result[i] = L[i]+ R[i];
		}
		return result;
	}
	public boolean judge(int[]count, int k) {
		for (int i = 0; i < count.length; i++) {
			if (count[i] < k && count[i] > 0) {return false;}
		}
		return true;
	}
	
	
	
	
	
	
    public int longestSubstring(String s, int k) {
    	char[]c = s.toCharArray();
    	Node root = maketree(c, 0, c.length-1);
    	int max = 0;
    	for (int start = 0; start < c.length; start++) {
    		if (c.length - start <max){break;}
    		for (int end = c.length-1; end >= start; end--) {
    			if (judge(countchar(root, start, end), k)) {
    				max = Math.max(max, end - start+1);
    				break;
    			}
    		}
    	}
    	return max;
    	
    	
    	
    }
    
    public static void main(String[]str) {
    	String  s = "";
    	int k =301;
    	System.out.println(new Solution().longestSubstring(s, k));
    }
}


